-- Volcado de datos para la tabla `wp_userposts`
--

INSERT INTO `wp_userposts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-07-17 05:36:39', '2017-07-17 05:36:39', 'Bienvenido a WordPress. Esta es tu primera entrada. EdÃ­tala o bÃ³rrala, Â¡y comienza a escribir!', 'Â¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2017-07-17 05:36:39', '2017-07-17 05:36:39', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2017-07-17 05:36:39', '2017-07-17 05:36:39', 'Esta es una pÃ¡gina de ejemplo, Es diferente a una entrada de un blog porque se mantiene estÃ¡tica y se mostrarÃ¡ en la barra de navegaciÃ³n (en la mayorÃ­a de temas). Casi todo el mundo comienza con una pÃ¡gina Sobre mÃ­ para presentarse a los potenciales visitantes. PodrÃ­a ser algo asÃ­:\n\n<blockquote>Â¡Hola!: Soy mensajero por el dÃ­a, aspirante a actor por la noche y esta es mi web. Vivo en Madrid, tengo perrazo llamado Juan y me encantan las piÃ±as coladas (y que me pille un chaparrÃ³n)</blockquote>\n\nâ€¦ o algo asÃ­:\n\n<blockquote>La empresa Calcetines XYZ se fundÃ³ en 1971, y ha estado produciendo calcetines de calidad para sus clientes desde entonces. Ubicada en Gothan, la empresa XYZ tiene mÃ¡s de 2.000 empleados e intenta ayudar en lo que puede para mejorar la vida en Gothan</blockquote>\n\nSi eres nuevo en WordPress deberÃ­as ir a <a href="http://localhost/wordpress/wp-admin/">tu escritorio</a> para borrar esta pÃ¡gina y crear algunas nuevas con tu contenido. Â¡PÃ¡salo bien!', 'PÃ¡gina de ejemplo', '', 'publish', 'closed', 'open', '', 'pagina-ejemplo', '', '', '2017-07-17 05:36:39', '2017-07-17 05:36:39', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2017-07-17 05:36:58', '0000-00-00 00:00:00', '', 'Borrador automÃ¡tico', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-07-17 05:36:58', '0000-00-00 00:00:00', '', 0, 'http://localhost/wordpress/?p=3', 0, 'post', '', 0),
